client/src/pages/admin/invoices.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { useQuery } from "@tanstack/react-query";
import { Search, Printer, FileDown, FileSpreadsheet } from "lucide-react";
import { useLanguage } from "@/lib/language-context";
import type { Invoice, InvoiceItem, CompanySettings } from "@shared/schema";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { exportInvoiceToPDF, exportInvoicesToExcel } from "@/lib/exports";
import { useToast } from "@/hooks/use-toast";

export default function Invoices() {
  const { language, t } = useLanguage();
  const [searchQuery, setSearchQuery] = useState("");
  const { toast } = useToast();

  const { data: invoices = [], isLoading } = useQuery<Invoice[]>({
    queryKey: ["/api/invoices"],
  });

  const { data: settings } = useQuery<CompanySettings>({
    queryKey: ["/api/settings"],
  });

  const filteredInvoices = invoices.filter((invoice) => {
    const query = searchQuery.toLowerCase();
    return (
      invoice.invoiceNumber.toLowerCase().includes(query) ||
      (invoice.customerName && invoice.customerName.toLowerCase().includes(query))
    );
  });

  const handlePrint = (invoiceId: string) => {
    window.open(`/admin/invoices/${invoiceId}/print`, "_blank");
  };

  const [isExporting, setIsExporting] = useState<string | null>(null);

  const handleExportPDF = async (invoiceId: string) => {
    if (!settings) {
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" ? "    " : "Company settings not loaded",
        variant: "destructive",
      });
      return;
    }

    setIsExporting(invoiceId);
    try {
      const invoiceResponse = await fetch(`/api/invoices/${invoiceId}`);
      if (!invoiceResponse.ok) {
        throw new Error("Failed to fetch invoice");
      }
      const invoice = await invoiceResponse.json();

      const itemsResponse = await fetch(`/api/invoices/${invoiceId}/items`);
      if (!itemsResponse.ok) {
        throw new Error("Failed to fetch invoice items");
      }
      const items: InvoiceItem[] = await itemsResponse.json();

      exportInvoiceToPDF({ ...invoice, items }, settings, language);

      toast({
        title: language === "bn" ? "" : "Success",
        description: language === "bn" ? "  " : "PDF downloaded successfully",
      });
    } catch (error) {
      console.error("PDF export error:", error);
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" ? "   " : "Failed to create PDF",
        variant: "destructive",
      });
    } finally {
      setIsExporting(null);
    }
  };

  const handleExportExcel = () => {
    exportInvoicesToExcel(filteredInvoices, language);
    toast({
      title: language === "bn" ? "" : "Success",
      description: language === "bn" ? "  " : "Excel downloaded successfully",
    });
  };

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-foreground">{t("admin.invoices")}</h1>
        <Button
          onClick={handleExportExcel}
          disabled={filteredInvoices.length === 0}
          data-testid="button-export-excel"
        >
          <FileSpreadsheet className="mr-2 h-4 w-4" />
          {language === "bn" ? " " : "Export Excel"}
        </Button>
      </div>

      <Card>
        <CardHeader>
          <div className="relative max-w-sm">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder={language === "bn" ? "   " : "Search invoice or customer"}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9"
              data-testid="input-search-invoices"
            />
          </div>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-3">
              {Array.from({ length: 5 }).map((_, i) => (
                <Skeleton key={i} className="h-16 w-full" />
              ))}
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>{language === "bn" ? " " : "Invoice No."}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Customer"}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Date"}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Type"}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Total"}</TableHead>
                  <TableHead className="text-right">{language === "bn" ? "" : "Actions"}</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredInvoices.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-center py-8 text-muted-foreground">
                      {language === "bn" ? "  " : "No invoices found"}
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredInvoices.map((invoice) => (
                    <TableRow key={invoice.id} data-testid={`row-invoice-${invoice.id}`}>
                      <TableCell className="font-medium">{invoice.invoiceNumber}</TableCell>
                      <TableCell>{invoice.customerName}</TableCell>
                      <TableCell>
                        {new Date(invoice.createdAt!).toLocaleDateString()}
                      </TableCell>
                      <TableCell>
                        <Badge variant={invoice.isPOS ? "secondary" : "default"}>
                          {invoice.isPOS ? "POS" : "Website"}
                        </Badge>
                      </TableCell>
                      <TableCell className="font-semibold">{invoice.total}</TableCell>
                      <TableCell className="text-right">
                        <div className="flex justify-end gap-2">
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={() => handleExportPDF(invoice.id)}
                            disabled={isExporting === invoice.id}
                            data-testid={`button-pdf-${invoice.id}`}
                          >
                            {isExporting === invoice.id ? (
                              <span className="animate-spin"></span>
                            ) : (
                              <FileDown className="h-4 w-4" />
                            )}
                          </Button>
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={() => handlePrint(invoice.id)}
                            data-testid={`button-print-${invoice.id}`}
                          >
                            <Printer className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import passport from "./auth";
import {
  insertProductSchema,
  insertOrderSchema,
  insertOrderItemSchema,
  insertInvoiceSchema,
  insertInvoiceItemSchema,
  insertCompanySettingsSchema,
  insertAdminUserSchema,
} from "@shared/schema";
import { z } from "zod";

// Authentication middleware
function requireAuth(req: Request, res: Response, next: NextFunction) {
  if (req.isAuthenticated()) {
    return next();
  }
  res.status(401).json({ error: "Unauthorized" });
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.post("/api/auth/login", (req, res, next) => {
    passport.authenticate("local", (err: Error, user: Express.User, info: { message?: string }) => {
      if (err) {
        return res.status(500).json({ message: "Internal server error" });
      }
      if (!user) {
        return res.status(401).json({ message: info?.message || "Invalid username or password" });
      }
      req.logIn(user, (err) => {
        if (err) {
          return res.status(500).json({ message: "Login failed" });
        }
        res.json({ user, message: "Login successful" });
      });
    })(req, res, next);
  });

  app.post("/api/auth/logout", (req, res) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ error: "Logout failed" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  app.get("/api/auth/me", (req, res) => {
    if (req.isAuthenticated()) {
      res.json({ user: req.user });
    } else {
      res.status(401).json({ error: "Not authenticated" });
    }
  });

  // Admin user management routes (protected)
  app.get("/api/admin/users", requireAuth, async (_req, res) => {
    try {
      const users = await storage.getAdminUsers();
      res.json(users);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch users" });
    }
  });

  app.post("/api/admin/users", requireAuth, async (req, res) => {
    try {
      const data = insertAdminUserSchema.parse(req.body);
      const user = await storage.createAdminUser(data);
      res.status(201).json(user);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      if (error instanceof Error && error.message.includes("unique")) {
        return res.status(400).json({ error: "Username already exists" });
      }
      res.status(500).json({ error: "Failed to create user" });
    }
  });

  app.delete("/api/admin/users/:id", requireAuth, async (req, res) => {
    try {
      // Prevent deleting yourself
      if (req.user && req.user.id === req.params.id) {
        return res.status(400).json({ error: "Cannot delete your own account" });
      }

      const deleted = await storage.deleteAdminUser(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "User not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete user" });
    }
  });

  // Products
  app.get("/api/products", async (_req, res) => {
    try {
      const products = await storage.getProducts();
      res.json(products);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch products" });
    }
  });

  app.get("/api/products/:id", async (req, res) => {
    try {
      const product = await storage.getProduct(req.params.id);
      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch product" });
    }
  });

  app.post("/api/products", async (req, res) => {
    try {
      // Convert empty string to undefined for optional decimal fields
      const body = {
        ...req.body,
        discountedPrice: req.body.discountedPrice === "" ? undefined : req.body.discountedPrice,
      };

      // Validate that discounted price is less than or equal to regular price
      if (body.discountedPrice !== undefined && body.discountedPrice !== null) {
        const regularPrice = parseFloat(body.price);
        const discountedPrice = parseFloat(body.discountedPrice);
        if (discountedPrice > regularPrice) {
          return res.status(400).json({ 
            error: "Discounted price cannot be greater than regular price" 
          });
        }
      }

      const data = insertProductSchema.parse(body);
      const product = await storage.createProduct(data);
      res.status(201).json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to create product" });
    }
  });

  app.patch("/api/products/:id", async (req, res) => {
    try {
      // Convert empty string to undefined for optional decimal fields
      const body = {
        ...req.body,
        discountedPrice: req.body.discountedPrice === "" ? undefined : req.body.discountedPrice,
      };

      // Validate that discounted price is less than or equal to regular price
      if (body.discountedPrice !== undefined && body.discountedPrice !== null) {
        // Fetch existing product to get current price if not provided in request
        const existingProduct = await storage.getProduct(req.params.id);
        if (!existingProduct) {
          return res.status(404).json({ error: "Product not found" });
        }

        // Use the price from the request if provided, otherwise use the existing price
        const regularPrice = body.price !== undefined 
          ? parseFloat(body.price) 
          : parseFloat(existingProduct.price.toString());
        const discountedPrice = parseFloat(body.discountedPrice);

        if (discountedPrice > regularPrice) {
          return res.status(400).json({ 
            error: "Discounted price cannot be greater than regular price" 
          });
        }
      }

      const data = insertProductSchema.partial().parse(body);
      const product = await storage.updateProduct(req.params.id, data);
      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update product" });
    }
  });

  app.delete("/api/products/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteProduct(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Product not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete product" });
    }
  });

  // Orders
  app.get("/api/orders", async (_req, res) => {
    try {
      const orders = await storage.getOrders();
      res.json(orders);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch orders" });
    }
  });

  app.get("/api/orders/:id", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.id);
      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch order" });
    }
  });

  app.get("/api/orders/:id/items", async (req, res) => {
    try {
      const items = await storage.getOrderItems(req.params.id);
      res.json(items);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch order items" });
    }
  });

  app.post("/api/orders", async (req, res) => {
    try {
      const { items, ...orderData } = req.body;

      const validatedOrder = insertOrderSchema.parse(orderData);
      const validatedItems = z.array(insertOrderItemSchema.omit({ orderId: true })).parse(items);

      // If order is being created as "confirmed", reduce stock immediately
      const reduceStock = validatedOrder.status === "confirmed";
      const order = await storage.createOrderWithItems(validatedOrder, validatedItems, reduceStock);

      res.status(201).json(order);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      if (error instanceof Error && error.message.includes("Insufficient stock")) {
        return res.status(400).json({ error: error.message });
      }
      res.status(500).json({ error: "Failed to create order" });
    }
  });

  app.patch("/api/orders/:id", async (req, res) => {
    try {
      const data = insertOrderSchema.partial().parse(req.body);

      if (data.status === "confirmed") {
        const order = await storage.getOrder(req.params.id);
        if (order) {
          const items = await storage.getOrderItems(order.id);
          for (const item of items) {
            const reduced = await storage.reduceStock(
              item.productId,
              item.quantity
            );
            if (!reduced) {
              return res.status(400).json({
                error: `Insufficient stock for product ${item.productNameEn}`,
              });
            }
          }
        }
      }

      const order = await storage.updateOrder(req.params.id, data);
      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update order" });
    }
  });

  // Invoices
  app.get("/api/invoices", async (_req, res) => {
    try {
      const invoices = await storage.getInvoices();
      res.json(invoices);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch invoices" });
    }
  });

  app.get("/api/invoices/:id", async (req, res) => {
    try {
      const invoice = await storage.getInvoice(req.params.id);
      if (!invoice) {
        return res.status(404).json({ error: "Invoice not found" });
      }
      res.json(invoice);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch invoice" });
    }
  });

  app.get("/api/invoices/:id/items", async (req, res) => {
    try {
      const items = await storage.getInvoiceItems(req.params.id);
      res.json(items);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch invoice items" });
    }
  });

  app.post("/api/invoices", async (req, res) => {
    try {
      const { items, ...invoiceData } = req.body;

      const invoiceNumber = await storage.getNextInvoiceNumber();

      const validatedInvoice = insertInvoiceSchema.parse({
        ...invoiceData,
        invoiceNumber,
      });

      const validatedItems = z.array(insertInvoiceItemSchema.omit({ invoiceId: true })).parse(items);

      // Create invoice with items and reduce stock in a single transaction
      const invoice = await storage.createInvoiceWithItems(validatedInvoice, validatedItems, true);

      res.status(201).json(invoice);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      if (error instanceof Error && error.message.includes("Insufficient stock")) {
        return res.status(400).json({ error: error.message });
      }
      res.status(500).json({ error: "Failed to create invoice" });
    }
  });

  // Company Settings
  app.get("/api/settings", async (_req, res) => {
    try {
      const settings = await storage.getSettings();
      res.json(settings);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch settings" });
    }
  });

  app.post("/api/settings", async (req, res) => {
    try {
      const data = insertCompanySettingsSchema.parse(req.body);
      const settings = await storage.updateSettings(data);
      res.json(settings);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update settings" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

