import { createContext, useContext, useState, useEffect } from "react";

type Language = "en" | "bn";

interface LanguageContextType {
  language: Language;
  setLanguage: (lang: Language) => void;
  t: (key: string) => string;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

const translations: Record<string, Record<Language, string>> = {
  // Navigation
  "nav.home": { en: "Home", bn: "" },
  "nav.products": { en: "Products", bn: "" },
  "nav.about": { en: "About", bn: "" },
  "nav.contact": { en: "Contact", bn: "" },

  // Hero
  "hero.title": { en: "Discover Your Beauty", bn: "   " },
  "hero.subtitle": { en: "Premium cosmetics delivered to your door", bn: "      " },
  "hero.cta": { en: "Shop Now", bn: " " },

  // Product
  "product.price": { en: "Price", bn: "" },
  "product.addToCart": { en: "Add to Cart", bn: "  " },
  "product.outOfStock": { en: "Out of Stock", bn: " " },
  "product.inStock": { en: "In Stock", bn: " " },
  "product.description": { en: "Description", bn: "" },

  // Checkout
  "checkout.title": { en: "Checkout", bn: "" },
  "checkout.customerInfo": { en: "Customer Information", bn: " " },
  "checkout.name": { en: "Full Name", bn: " " },
  "checkout.phone": { en: "Phone Number", bn: " " },
  "checkout.address": { en: "Full Address", bn: " " },
  "checkout.deliveryLocation": { en: "Delivery Location", bn: " " },
  "checkout.insideDhaka": { en: "Inside Dhaka", bn: " " },
  "checkout.outsideDhaka": { en: "Outside Dhaka", bn: " " },
  "checkout.orderSummary": { en: "Order Summary", bn: " " },
  "checkout.subtotal": { en: "Subtotal", bn: "" },
  "checkout.delivery": { en: "Delivery Charge", bn: " " },
  "checkout.total": { en: "Total", bn: "" },
  "checkout.placeOrder": { en: "Place Order", bn: " " },
  "checkout.cod": { en: "Cash on Delivery (COD)", bn: "  " },

  // Footer
  "footer.about": { en: "About", bn: "" },
  "footer.quickLinks": { en: "Quick Links", bn: " " },
  "footer.contact": { en: "Contact", bn: "" },
  "footer.payment": { en: "Payment & Delivery", bn: "  " },
  "footer.copyright": { en: " LUNAVEIL  All Rights Reserved", bn: "    " },

  // Admin
  "admin.dashboard": { en: "Dashboard", bn: "" },
  "admin.products": { en: "Products", bn: "" },
  "admin.orders": { en: "Orders", bn: "" },
  "admin.pos": { en: "POS Billing", bn: " " },
  "admin.invoices": { en: "Invoices", bn: "" },
  "admin.users": { en: "Users", bn: "" },
  "admin.settings": { en: "Settings", bn: "" },

  // Common
  "common.search": { en: "Search", bn: "" },
  "common.cancel": { en: "Cancel", bn: "" },
  "common.save": { en: "Save", bn: "" },
  "common.delete": { en: "Delete", bn: "" },
  "common.edit": { en: "Edit", bn: "" },
  "common.loading": { en: "Loading...", bn: " ..." },
  "common.quantity": { en: "Quantity", bn: "" },
};

export function LanguageProvider({ children }: { children: React.ReactNode }) {
  const [language, setLanguage] = useState<Language>("en");

  useEffect(() => {
    const saved = localStorage.getItem("language") as Language;
    if (saved && (saved === "en" || saved === "bn")) {
      setLanguage(saved);
    }
  }, []);

  const handleSetLanguage = (lang: Language) => {
    setLanguage(lang);
    localStorage.setItem("language", lang);
  };

  const t = (key: string) => {
    return translations[key]?.[language] || key;
  };

  return (
    <LanguageContext.Provider value={{ language, setLanguage: handleSetLanguage, t }}>
      {children}
    </LanguageContext.Provider>
  );
}

export function useLanguage() {
  const context = useContext(LanguageContext);
  if (!context) {
    throw new Error("useLanguage must be used within LanguageProvider");
  }
  return context;
}

