import { useState, useEffect } from "react";
import { CustomerHeader } from "@/components/customer-header";
import { CustomerFooter } from "@/components/customer-footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useLanguage } from "@/lib/language-context";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { Product } from "@shared/schema";
import { ShoppingBag } from "lucide-react";

interface CartItem extends Product {
  quantity: number;
}

export default function Checkout() {
  const { language, t } = useLanguage();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [cart, setCart] = useState<CartItem[]>([]);

  const [formData, setFormData] = useState({
    customerName: "",
    customerPhone: "",
    customerAddress: "",
    deliveryLocation: "inside_dhaka" as "inside_dhaka" | "outside_dhaka",
  });

  const { data: settings } = useQuery({
    queryKey: ["/api/settings"],
  });

  useEffect(() => {
    const savedCart = localStorage.getItem("cart");
    if (savedCart) {
      try {
        setCart(JSON.parse(savedCart));
      } catch (e) {
        console.error("Failed to parse cart", e);
      }
    }
  }, []);

  const deliveryCharge =
    formData.deliveryLocation === "inside_dhaka"
      ? parseFloat(settings?.deliveryChargeInsideDhaka || "60")
      : parseFloat(settings?.deliveryChargeOutsideDhaka || "120");

  const subtotal = cart.reduce(
    (sum, item) => {
      const price = item.discountedPrice 
        ? parseFloat(item.discountedPrice.toString()) 
        : parseFloat(item.price.toString());
      return sum + price * item.quantity;
    },
    0
  );

  const total = subtotal + deliveryCharge;

  const orderMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/orders", data);
    },
    onSuccess: () => {
      toast({
        title: language === "bn" ? " !" : "Order Successful!",
        description: language === "bn" 
          ? "    " 
          : "Your order has been received.",
      });
      localStorage.removeItem("cart");
      setTimeout(() => setLocation("/"), 2000);
    },
    onError: () => {
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" 
          ? "   " 
          : "Failed to process order.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.customerName || !formData.customerPhone || !formData.customerAddress) {
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" 
          ? "   " 
          : "Please fill all fields.",
        variant: "destructive",
      });
      return;
    }

    if (cart.length === 0) {
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" 
          ? "  " 
          : "Your cart is empty.",
        variant: "destructive",
      });
      return;
    }

    const orderData = {
      ...formData,
      deliveryCharge: deliveryCharge.toString(),
      subtotal: subtotal.toString(),
      total: total.toString(),
      status: "pending",
      items: cart.map((item) => {
        const regularPrice = parseFloat(item.price.toString());
        const effectivePrice = item.discountedPrice 
          ? parseFloat(item.discountedPrice.toString()) 
          : regularPrice;
        return {
          productId: item.id,
          productNameEn: item.nameEn,
          productNameBn: item.nameBn,
          quantity: item.quantity,
          regularPrice: regularPrice.toString(),
          price: effectivePrice.toString(),
          subtotal: (effectivePrice * item.quantity).toString(),
        };
      }),
    };

    orderMutation.mutate(orderData);
  };

  if (cart.length === 0) {
    return (
      <div className="min-h-screen bg-background flex flex-col">
        <CustomerHeader />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <ShoppingBag className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
            <h2 className="text-2xl font-semibold mb-2">
              {language === "bn" ? "  " : "Your cart is empty"}
            </h2>
            <Button onClick={() => setLocation("/")} data-testid="button-back-home">
              {language === "bn" ? "  " : "Continue Shopping"}
            </Button>
          </div>
        </div>
        <CustomerFooter />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background flex flex-col">
      <CustomerHeader />

      <main className="flex-1 container mx-auto px-4 py-12">
        <h1 className="text-4xl font-serif font-bold mb-8 text-center text-foreground">
          {t("checkout.title")}
        </h1>

        <div className="grid lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>{t("checkout.customerInfo")}</CardTitle>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleSubmit} className="space-y-6">
                  <div className="space-y-2">
                    <Label htmlFor="name">{t("checkout.name")}</Label>
                    <Input
                      id="name"
                      value={formData.customerName}
                      onChange={(e) =>
                        setFormData({ ...formData, customerName: e.target.value })
                      }
                      required
                      data-testid="input-customer-name"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="phone">{t("checkout.phone")}</Label>
                    <Input
                      id="phone"
                      type="tel"
                      value={formData.customerPhone}
                      onChange={(e) =>
                        setFormData({ ...formData, customerPhone: e.target.value })
                      }
                      required
                      data-testid="input-customer-phone"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="address">{t("checkout.address")}</Label>
                    <Textarea
                      id="address"
                      value={formData.customerAddress}
                      onChange={(e) =>
                        setFormData({ ...formData, customerAddress: e.target.value })
                      }
                      required
                      rows={3}
                      data-testid="input-customer-address"
                    />
                  </div>

                  <div className="space-y-3">
                    <Label>{t("checkout.deliveryLocation")}</Label>
                    <RadioGroup
                      value={formData.deliveryLocation}
                      onValueChange={(value) =>
                        setFormData({
                          ...formData,
                          deliveryLocation: value as "inside_dhaka" | "outside_dhaka",
                        })
                      }
                    >
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="inside_dhaka" id="inside" data-testid="radio-inside-dhaka" />
                        <Label htmlFor="inside" className="font-normal cursor-pointer">
                          {t("checkout.insideDhaka")} ({settings?.deliveryChargeInsideDhaka || "60"})
                        </Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="outside_dhaka" id="outside" data-testid="radio-outside-dhaka" />
                        <Label htmlFor="outside" className="font-normal cursor-pointer">
                          {t("checkout.outsideDhaka")} ({settings?.deliveryChargeOutsideDhaka || "120"})
                        </Label>
                      </div>
                    </RadioGroup>
                  </div>

                  <Button
                    type="submit"
                    className="w-full"
                    size="lg"
                    disabled={orderMutation.isPending}
                    data-testid="button-submit-order"
                  >
                    {orderMutation.isPending
                      ? t("common.loading")
                      : t("checkout.placeOrder")}
                  </Button>
                </form>
              </CardContent>
            </Card>
          </div>

          <div>
            <Card className="sticky top-24">
              <CardHeader>
                <CardTitle>{t("checkout.orderSummary")}</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {cart.map((item) => {
                  const name = language === "bn" ? item.nameBn : item.nameEn;
                  const price = item.discountedPrice 
                    ? parseFloat(item.discountedPrice.toString()) 
                    : parseFloat(item.price.toString());
                  return (
                    <div key={item.id} className="flex justify-between text-sm" data-testid={`summary-item-${item.id}`}>
                      <span className="text-muted-foreground">
                        {name}  {item.quantity}
                      </span>
                      <span className="font-medium">
                        {(price * item.quantity).toFixed(2)}
                      </span>
                    </div>
                  );
                })}

                <div className="border-t pt-4 space-y-2">
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">{t("checkout.subtotal")}</span>
                    <span className="font-medium" data-testid="text-checkout-subtotal">{subtotal.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">{t("checkout.delivery")}</span>
                    <span className="font-medium" data-testid="text-checkout-delivery">{deliveryCharge.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between text-lg font-semibold border-t pt-2">
                    <span>{t("checkout.total")}</span>
                    <span className="text-primary" data-testid="text-checkout-total">{total.toFixed(2)}</span>
                  </div>
                </div>

                <div className="bg-muted p-3 rounded-md text-sm text-center">
                  {t("checkout.cod")}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </main>

      <CustomerFooter />
    </div>
  );
}

client/src/pages/admin/products.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Plus, Pencil, Trash2, Search, X, ImagePlus, FileSpreadsheet } from "lucide-react";
import { useLanguage } from "@/lib/language-context";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";
import { Skeleton } from "@/components/ui/skeleton";
import { exportProductsToExcel } from "@/lib/exports";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertProductSchema } from "@shared/schema";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormDescription,
} from "@/components/ui/form";
import { z } from "zod";

const formSchema = insertProductSchema.extend({
  price: z.string().min(1),
  discountedPrice: z.string().optional(),
  stock: z.string().min(1),
});

type FormData = z.infer<typeof formSchema>;

export default function Products() {
  const { language, t } = useLanguage();
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [newImageUrl, setNewImageUrl] = useState("");

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      nameEn: "",
      nameBn: "",
      descriptionEn: "",
      descriptionBn: "",
      price: "",
      discountedPrice: "",
      stock: "",
      category: "",
      images: [],
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/products", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: language === "bn" ? "  " : "Product added" });
      setDialogOpen(false);
      form.reset();
    },
    onError: (error: any) => {
      console.error("Create product error:", error);
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" ? "   " : "Failed to add product",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await apiRequest("PATCH", `/api/products/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: language === "bn" ? "  " : "Product updated" });
      setDialogOpen(false);
      setEditingProduct(null);
      form.reset();
    },
    onError: (error: any) => {
      console.error("Update product error:", error);
      toast({
        title: language === "bn" ? "" : "Error",
        description: language === "bn" ? "   " : "Failed to update product",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => apiRequest("DELETE", `/api/products/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: language === "bn" ? "   " : "Product deleted" });
    },
  });

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    form.reset({
      nameEn: product.nameEn,
      nameBn: product.nameBn,
      descriptionEn: product.descriptionEn,
      descriptionBn: product.descriptionBn,
      price: product.price.toString(),
      discountedPrice: product.discountedPrice?.toString() || "",
      stock: product.stock.toString(),
      category: product.category,
      images: product.images || [],
    });
    setDialogOpen(true);
  };

  const onSubmit = (data: FormData) => {
    // For Drizzle decimal fields, keep price as string
    // Only convert stock to number since it's an integer
    const productData = {
      ...data,
      stock: parseInt(data.stock, 10),
    };

    if (editingProduct) {
      updateMutation.mutate({ id: editingProduct.id, data: productData });
    } else {
      createMutation.mutate(productData);
    }
  };

  const filteredProducts = products.filter((product) => {
    const query = searchQuery.toLowerCase();
    return (
      product.nameEn.toLowerCase().includes(query) ||
      product.nameBn.includes(query) ||
      product.category.toLowerCase().includes(query)
    );
  });

  const handleExportExcel = () => {
    exportProductsToExcel(filteredProducts, language);
    toast({
      title: language === "bn" ? "" : "Success",
      description: language === "bn" ? "  " : "Excel downloaded successfully",
    });
  };

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-foreground">{t("admin.products")}</h1>
        <div className="flex gap-2">
          <Button
            variant="outline"
            onClick={handleExportExcel}
            disabled={filteredProducts.length === 0}
            data-testid="button-export-products-excel"
          >
            <FileSpreadsheet className="mr-2 h-4 w-4" />
            {language === "bn" ? " " : "Export Excel"}
          </Button>
          <Dialog
            open={dialogOpen}
            onOpenChange={(open) => {
              setDialogOpen(open);
              if (!open) {
                setEditingProduct(null);
                setNewImageUrl("");
                form.reset();
              }
            }}
          >
            <DialogTrigger asChild>
              <Button data-testid="button-add-product">
                <Plus className="h-4 w-4 mr-2" />
                {language === "bn" ? "  " : "Add Product"}
              </Button>
            </DialogTrigger>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>
                {editingProduct
                  ? language === "bn"
                    ? " "
                    : "Edit Product"
                  : language === "bn"
                  ? " "
                  : "New Product"}
              </DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="nameEn"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name (English)</FormLabel>
                        <FormControl>
                          <Input {...field} data-testid="input-name-en" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="nameBn"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name (Bengali)</FormLabel>
                        <FormControl>
                          <Input {...field} data-testid="input-name-bn" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="descriptionEn"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description (English)</FormLabel>
                        <FormControl>
                          <Textarea {...field} rows={3} data-testid="input-description-en" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="descriptionBn"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description (Bengali)</FormLabel>
                        <FormControl>
                          <Textarea {...field} rows={3} data-testid="input-description-bn" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="price"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Regular Price ()</FormLabel>
                        <FormControl>
                          <Input type="number" step="0.01" {...field} data-testid="input-price" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="discountedPrice"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Discounted Price () - Optional</FormLabel>
                        <FormControl>
                          <Input type="number" step="0.01" {...field} data-testid="input-discounted-price" />
                        </FormControl>
                        <FormDescription className="text-xs">
                          Leave empty if no discount
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="stock"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Stock</FormLabel>
                        <FormControl>
                          <Input type="number" {...field} data-testid="input-stock" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="category"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Category</FormLabel>
                        <FormControl>
                          <Input {...field} data-testid="input-category" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="images"
                  render={({ field }) => {
                    const handleAddImage = () => {
                      const url = newImageUrl.trim();
                      const currentImages = field.value || [];
                      if (url && !currentImages.includes(url)) {
                        field.onChange([...currentImages, url]);
                        setNewImageUrl("");
                      }
                    };

                    const handleRemoveImage = (index: number) => {
                      const currentImages = field.value || [];
                      const newImages = currentImages.filter((_, i) => i !== index);
                      field.onChange(newImages);
                    };

                    return (
                      <FormItem>
                        <FormLabel>
                          {language === "bn" ? " " : "Product Images"}
                        </FormLabel>
                        <FormControl>
                          <div className="space-y-3">
                            <div className="flex gap-2">
                              <Input
                                placeholder={language === "bn" ? " URL  " : "Enter image URL"}
                                value={newImageUrl}
                                onChange={(e) => setNewImageUrl(e.target.value)}
                                onKeyDown={(e) => {
                                  if (e.key === "Enter") {
                                    e.preventDefault();
                                    handleAddImage();
                                  }
                                }}
                                data-testid="input-image-url"
                              />
                              <Button
                                type="button"
                                variant="outline"
                                onClick={handleAddImage}
                                data-testid="button-add-image"
                              >
                                <ImagePlus className="h-4 w-4" />
                              </Button>
                            </div>

                            {field.value && field.value.length > 0 && (
                              <div className="grid grid-cols-3 gap-3" data-testid="image-preview-grid">
                                {field.value.map((url, index) => (
                                  <div key={index} className="relative group" data-testid={`image-preview-${index}`}>
                                    <img
                                      src={url}
                                      alt={`Product ${index + 1}`}
                                      className="w-full h-32 object-cover rounded-md border"
                                      onError={(e) => {
                                        e.currentTarget.src = "https://via.placeholder.com/150?text=Invalid+URL";
                                      }}
                                      data-testid={`img-product-preview-${index}`}
                                    />
                                    <Button
                                      type="button"
                                      size="icon"
                                      variant="destructive"
                                      className="absolute top-1 right-1 h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity"
                                      onClick={() => handleRemoveImage(index)}
                                      data-testid={`button-remove-image-${index}`}
                                    >
                                      <X className="h-3 w-3" />
                                    </Button>
                                  </div>
                                ))}
                              </div>
                            )}

                            <FormDescription>
                              {language === "bn" 
                                ? " URL    Enter   +         "
                                : "Enter image URL and press Enter or click the + button. You can add multiple images."}
                            </FormDescription>
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    );
                  }}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={createMutation.isPending || updateMutation.isPending}
                  data-testid="button-submit-product"
                >
                  {createMutation.isPending || updateMutation.isPending
                    ? t("common.loading")
                    : t("common.save")}
                </Button>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
        </div>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center gap-4">
            <div className="relative flex-1 max-w-sm">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder={t("common.search")}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
                data-testid="input-search-products"
              />
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-3">
              {Array.from({ length: 5 }).map((_, i) => (
                <Skeleton key={i} className="h-16 w-full" />
              ))}
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>{language === "bn" ? "" : "Name"}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Category"}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Price"}</TableHead>
                  <TableHead>{language === "bn" ? "" : "Stock"}</TableHead>
                  <TableHead className="text-right">
                    {language === "bn" ? "" : "Actions"}
                  </TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredProducts.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={5} className="text-center py-8 text-muted-foreground">
                      {language === "bn" ? "  " : "No products found"}
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredProducts.map((product) => (
                    <TableRow key={product.id} data-testid={`row-product-${product.id}`}>
                      <TableCell className="font-medium">
                        {language === "bn" ? product.nameBn : product.nameEn}
                      </TableCell>
                      <TableCell>{product.category}</TableCell>
                      <TableCell>{product.price}</TableCell>
                      <TableCell>
                        <span
                          className={
                            product.stock < 10 ? "text-orange-600 font-semibold" : ""
                          }
                        >
                          {product.stock}
                        </span>
                      </TableCell>
                      <TableCell className="text-right">
                        <div className="flex justify-end gap-2">
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={() => handleEdit(product)}
                            data-testid={`button-edit-${product.id}`}
                          >
                            <Pencil className="h-4 w-4" />
                          </Button>
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={() => deleteMutation.mutate(product.id)}
                            data-testid={`button-delete-${product.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

